mapper.select:
select count(*) 
from member
where userid = #{userid}

select passwd
from member
where userid = #{userid}

mapper.update:
update member 
set 
total_amount = total_amount + #{total_amount},
point = point + #{point}
where userid = #{userid}

mapper.select:
select total_amount
from member
